         score += 3;
      }
      //add 5 to score if 6.5 <= L <= 8.4
      if ( (6.5 <= L ) && ( L <= 8.4)) {
         score += 5;
      }
         
     //add 8 to score if L >= 8.5

      if ( L >= 8.5) {
         score += 8;
      }

      return score;
}
 
 
else { 
