function catchild(context) {
    var L = getLDL(context);
    
    function anyrel(context) {
      return ( (context.CDE00003 == "y_firstdegrel") || ( context.CDE00004 == "fh2_y") || ( context.FHFamHistTendonXanthoma == "fh2_y") || ( context.FHFamHistArcusCornealis == "fh2_y" ) );
    }
     
    if (context.CDE00025 == "gen_yes") {
        return "Definite";
     }
     //Definite if DNA Analysis is Yes
   //other wise
   else if (L > 5.0) {
     return "Highly Probable";
   }
   else if ( (L > 4.0) && anyrel(context)) {
     return "Highly Probable";
   }
   
   else if ( (L > 3.3) && ( L <= 4.0) && anyrel(context) ) {
      return "Possible";
   }
   else {
     return "Unlikely";
   }
   
}

function catadult(score) {
 
    if ((0 <= score) && (score <= 2)) {
        return "Unlikely";
    }
    
    if ((3 <= score) && (score <= 5)) {
        return "Possible";
    }
    
    if ((6 <= score) && (score <= 8)) {
       return "Probable";
    }
    
    return "Definite";
}

function categorise(context,patient) {
  var isAdult = patientAge(patient.date_of_birth) >= 18.0;
  
  if (isAdult) {
     return catadult(getscore(context,patient));
  }
  
  else {
     return catchild(context);
  }
  
}





